import React, { useState, useEffect, useContext } from 'react'
import { getDocs, query, where } from 'firebase/firestore'
import { collection, } from 'firebase/firestore'
import { Context } from '../../index'
import DialogElement from './DialogElement'

export default function ChatList() {
    const [dialogs, setDialogs] = useState([])
    const { db } = useContext(Context)
    const dialogsCollectionRef = collection(db, 'dialogs')

    useEffect(async () => {
        const q = query(dialogsCollectionRef)
        const t = await getDocs(q)
        setDialogs(t.docs.map(doc => ({ ...doc.data(), id: doc.id })))
        console.log(t.docs.map(doc => ({ ...doc.data(), id: doc.id })))
    }, [])

    return (
        <ul id="chat-list" className="chat-list visible">
            <form name="dialog" className="chat-add-dialog">
                <input className="chat-add-dialog--textarea" name="text" maxLength="18" placeholder="Nickname..." />
                <input className="chat-add-dialog--button" type="button" id="button-create" value="+" />
            </form>
            {
                dialogs.map(dialog => (
                    <DialogElement
                        key={dialog.id}
                        secondUsername={dialog.secondUsername}
                        lastMessage={dialog.secondUsername}
                        lastDate={dialog.secondUsername}
                    />
                ))
            }
        </ul>
    )
}
