import React, { useState, useEffect, useContext } from 'react'
import { getDocs, query, where } from 'firebase/firestore'
import { collection, doc, deleteDoc} from 'firebase/firestore'
import { Context } from '../../index'
import MessageElement from './MessageElement'
import { useAuthState } from 'react-firebase-hooks/auth'

export default function ChatMessages() {
    const [messages, setMessages] = useState([])
    const { db, auth } = useContext(Context)
    const [user] = useAuthState(auth)
    const messagesCollectionRef = collection(db, 'messages')

    useEffect(async () => {
        const q = query(messagesCollectionRef)
        const t = await getDocs(q)
        setMessages(t.docs.map(doc => ({ ...doc.data(), id: doc.id })))
    }, [])

    const submitDelete = async (event) => {
        event.preventDefault()
        const challengeDoc = doc(db, "messages", messages[0].id)
        await deleteDoc(challengeDoc)
        setMessages(messages.filter((msg) => msg.id !== messages[0].id))
    }

    return (
        <ul id="chat--messages">
            {
                messages.map(message => (
                    <MessageElement
                        key={message.id}
                        text={message.text}
                        date={message.date}
                        authorName={message.authorName}
                        mainUsername={user.email}
                    />
                ))
            }
        </ul>
    )
}
