import ChatMessages from '../components/HomePageComponents/ChatMessages'
import React, { useState, useEffect, useContext, useRef } from 'react'
import { getDocs, query } from 'firebase/firestore'
import { collection, } from 'firebase/firestore'
import { Context } from '../index'
import DialogElement from '../components/HomePageComponents/DialogElement'

import '../Home.css';

export default function Home() {
    const [user] = useAuthState(auth)
    const [dialogs, setDialogs] = useState([])
    const { db, auth } = useContext(Context)
    const dialogsCollectionRef = collection(db, 'dialogs')
    const dialogRef = useRef()

    useEffect(async () => {
        const q = query(dialogsCollectionRef)
        const t = await getDocs(q)
        setDialogs(t.docs.map(doc => ({ ...doc.data(), id: doc.id })))
    }, [])

    const submitCreateDialog = async (event) => {
        if (dialogRef.current.value === '') return
        var timestamp = (new Date()).getTime();
        var dialog = { secondUsername: dialogRef.current.value, firstUsername: user.email, lastDate: timestamp }
        let ref = await addDoc(dialogsCollectionRef, dialog)
        message.id = ref.id
        setMessages((prevDialogs) => [...prevDialogs, dialog])
        // textFieldRef.current.value = null
    }

    return (
        <main className='container'>
            <ul id="chat-list" className="chat-list visible">
                <form name="dialog" className="chat-add-dialog">
                    <input className="chat-add-dialog--textarea" ref={dialogRef} name="text" maxLength="18" placeholder="Nickname..." />
                    <input className="chat-add-dialog--button" type="submit" id="button-create" value="+" />
                </form>
                {
                    dialogs.map(dialog => (
                        <DialogElement
                            key={dialog.id}
                            secondUsername={dialog.secondUsername}
                            lastMessage={dialog.lastMessage}
                            lastDate={dialog.lastDate}
                        />
                    ))
                }
            </ul>
            <ChatMessages />
        </main>
    )
}
