import React from 'react'

export default function MessageElement({ text, date, authorName, mainUsername }) {
    const messageClass = authorName === mainUsername ? "message-right" : "message-left"

    const onClick = () => {
        messageClass = messageClass === "message-right" ? "message-left" : "message-right";
    }

    return (
        <div className={messageClass} onClick={onClick}>
            <p>{text}</p>
            <time className="hint">{date}</time>
        </div>
    )
}
