import React, { useState, useEffect, useContext } from 'react'
import { getDocs, query } from 'firebase/firestore'
import { collection, doc, deleteDoc, addDoc } from 'firebase/firestore'
import { Context } from '../../index'
import { useAuthState } from 'react-firebase-hooks/auth'

export default function ChatMessages() {
    const [messages, setMessages] = useState([])
    const [textField, setTextField] = useState([])
    const { db, auth } = useContext(Context)
    const [user] = useAuthState(auth)
    const messagesCollectionRef = collection(db, 'messages')
    var selectedMessages = [];

    useEffect(async () => {
        const q = query(messagesCollectionRef)
        const t = await getDocs(q)
        setMessages(t.docs.map(doc => ({ ...doc.data(), id: doc.id })).sort(a, b => {return a.date < b.date}))
    }, [])

    const submitDeleteMessage = async (event) => {
        event.preventDefault()
        const challengeDoc = doc(db, "messages", messages[0].id)
        await deleteDoc(challengeDoc)
        setMessages(messages.filter((msg) => msg.id !== messages[0].id))
    }

    const submitSendMessage = async (event) => {
        event.preventDefault()
        var now = new Date();
        let time = `${now.getHours()}:${now.getMinutes()}`
        await addDoc(messagesCollectionRef, { text: textField.text, authorName: user.email, date: time })
        setMessages(messages.filter((msg) => msg.id !== messages[0].id))
    }

    const handleTextFieldChange = (event) => {
        setTextField(textField => ({ ...textField, [event.target.name]: event.target.value }))
    }


    const onMessageClick = (id) => {
        var existId = selectedMessages.indexOf(id)
        if (existId !== -1) {
            selectedMessages.splice(existId)
        } else {
            selectedMessages.push(existId)
        }
    }

    return (
        <section className='chat'>
            <div className="chat--header">
                <button id="display-dialogs-button" className="display-dialogs-button">-</button>
                <p id="current-user"></p>
                <button id="delete-button" className="delete-button" onClick={submitDeleteMessage}>Delete</button>
            </div>
            <ul id="chat--messages">
                {
                    messages.map((message) => {
                        const messageClass = message.authorName === user.email ? "message-right" : "message-left"
                        return (
                            <div className={messageClass} onClick={() => onMessageClick(message.id)}>
                                <p>{message.text}</p>
                                <time className="hint">{message.date}</time>
                            </div>
                        )
                    })
                }
            </ul>
            <form name="message" className="chat--footer-textarea">
                <textarea name="text" className="message-text" rows="3" onChange={handleTextFieldChange} placeholder="Write a message..."></textarea>
                <input type="button" id="button-send" value="Send" onClick={submitSendMessage} />
            </form>
        </section>
    )
}
